ModSqrtEasy := proc(a,p)
  local sqrtA, b;
  sqrtA := sqrt(a);
  if (LegendreSymbol(sqrtA,p) = 1) then
    b := ((a^((p+1)/4)) mod p);
    print(b);
    print(p-b);
    return ((a^((p+1)/4)) mod p);
  end if;
end:

