tonelli_shanks := proc(n, p)
  local s, q, count, z, m, c, t, R, i, b;
  s := 0;
  q := p - 1;

  while ((q mod 2) = 0) do
    q := q/2;
    s := s+1;
  end do;

  count := 0;
  z := 0;

  while (count <> (p-1)) do
    z := z+1;
    count := Power(z,(p-1)/2) mod p;
  end do;

  m := s;
  c := z^q mod p;
  t := n^q mod p;
  R := n^((q+1)/2) mod p;
  i := 1;

  while (t <> 1) do
    i := i+1;
    b := Power(c,2^(m-i-1) mod p) mod p;
    m := i;
    c := (b^2) mod p;
    t := (t * (b^2)) mod p;
    R := (R * b) mod p;
  end do;

  print(R);
  print(p-R);
  return R;
